mapper.insert:
INSERT INTO MEMBER_INFO(
                         LOGIN_ID, MEMBER_PWD
                       , MEMBER_EMAIL, MEMBER_TEL_NO, MEMBER_NICKNM
                       , MEMBER_ST, CRT_DT
) VALUES (
           #{login_id}, #{member_pwd}
         , #{member_email}, #{member_tel_no}, #{member_nicknm}
         , #{member_st}, NOW()
)

mapper.select:
SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE LOGIN_ID = #{login_id}

SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE MEMBER_EMAIL = #{member_email}

SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE MEMBER_NICKNM = #{member_nicknm}
