mapper.insert:
INSERT INTO MEMBER_INFO(
                         LOGIN_ID, MEMBER_PWD
                       , MEMBER_EMAIL, MEMBER_TEL_NO, MEMBER_NICKNM
                       , CRT_DTM
) VALUES (
           #{login_id}, #{member_pwd}
         , #{member_email}, #{member_tel_no}, #{member_nicknm}
         , NOW()
)

INSERT INTO MEMBER_INFO (
    LOGIN_ID, MEMBER_EMAIL, MEMBER_PWD, MEMBER_NICKNM, CRT_DTM
) VALUES (
             #{login_id}, #{email}, #{member_pwd}, #{nickname}, NOW()
         )

INSERT INTO MEMBER_OAUTH (
    MEMBER_SEQ, PROVIDER, PROVIDER_ID, CONNECTED_DT
) VALUES (
             #{member_seq}, #{provider}::oauth_provider_enum, #{provider_id}, NOW()
         )

mapper.select:
SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE LOGIN_ID = #{login_id}

SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE MEMBER_EMAIL = #{member_email}

SELECT COUNT(*)
  FROM MEMBER_INFO
 WHERE MEMBER_NICKNM = #{member_nicknm}
