mapper.insert:
INSERT INTO NOTICE (
       ADMIN_SEQ
     , NOTICE_TITLE
     , NOTICE_PRIORITY
     , ACTIVE
     , NOTICE_CONT
     , CRT_DTM
) VALUES (
       #{admin_seq}
     , #{notice_title}
     , #{notice_priority}
     , #{active}
     , #{notice_cont}
     , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE NOTICE
   SET VIEW_COUNT = VIEW_COUNT + 1
 WHERE NOTICE_SEQ = #{notice_seq}
