mapper.select:
SELECT COUNT(*)
FROM book_club_member
WHERE book_club_seq = #{bookClubSeq}
  AND member_seq = #{memberSeq}
  AND join_st = 'JOINED'

SELECT COUNT(*)
FROM book_club_request
WHERE book_club_seq = #{bookClubSeq}
  AND request_member_seq = #{memberSeq}
  AND request_st = 'WAIT'

SELECT COUNT(*)
FROM book_club_wish
WHERE book_club_seq = #{bookClubSeq}
