mapper.insert:
INSERT INTO chatroom (
trade_seq,
crt_dtm,
member_buyer_seq,
member_seller_seq,
sale_title
) VALUES (
#{trade_seq}, now(), #{member_buyer_seq}, #{member_seller_seq}, #{sale_title}
)

mapper.select:
SELECT c.*, t.sale_st
FROM chatroom c
LEFT JOIN sb_trade_info t ON c.trade_seq = t.trade_seq
WHERE c.member_buyer_seq = #{member_seq}
OR c.member_seller_seq = #{member_seq}
ORDER BY c.crt_dtm DESC

SELECT c.*, t.sale_st, seller.member_nicknm AS member_seller_nicknm, buyer.member_nicknm AS member_buyer_nicknm, COALESCE(unread.unread_count, 0) > 0 AS msg_unread FROM chatroom c LEFT JOIN sb_trade_info t ON c.trade_seq = t.trade_seq LEFT JOIN member_info seller ON c.member_seller_seq = seller.member_seq LEFT JOIN member_info buyer ON c.member_buyer_seq = buyer.member_seq LEFT JOIN ( SELECT chat_room_seq, COUNT(*) AS unread_count FROM chat_msg WHERE read_yn = false AND sender_seq != #{member_seq} GROUP BY chat_room_seq ) unread ON c.chat_room_seq = unread.chat_room_seq WHERE (c.member_seller_seq = #{member_seq} OR c.member_buyer_seq = #{member_seq})
AND t.sale_st = #{sale_st}::sale_st_enum
ORDER BY c.crt_dtm DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) > 0
FROM chatroom
WHERE chat_room_seq = #{chat_room_seq}
AND (member_buyer_seq = #{member_seq}
OR member_seller_seq = #{member_seq})

SELECT chat_room_seq
FROM chatroom
WHERE
    trade_seq = #{trade_seq}
and member_buyer_seq = #{buyer_seq}
LIMIT 1

SELECT COUNT(*) > 0
FROM chatroom
WHERE trade_seq = #{trade_seq}
  AND member_buyer_seq = #{member_seq}
